package tray

import (
	"encoding/base64"
	"io/ioutil"
	"os"
)

// Icons in b64 format (I know there are better ways to do this, but ok, let's not argue about 2 bytes)
const (
	iconOn    = ``
	iconOff   = ``
	iconError = ``
	iconOk    = `PHN2ZyB3aWR0aD0iNDgiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyIgaGVpZ2h0PSI0OCIgdmlld0JveD0iMCAwIDQ4IDQ4IiB4bWxuczp4bGluaz0iaHR0cDovL3d3dy53My5vcmcvMTk5OS94bGluayI+CjxwYXRoIHN0eWxlPSJmaWxsOiM4NTk5MDAiIGQ9Ik0gMTggMjYgTCAzOCA4IEwgNDQgMTYgTCAxOCA0MCBMIDQgMjYgTCAxMCAxOCBaICIvPgo8L3N2Zz4K`
)

// converts b64 icons to byte slices
func getIcons() (map[string][]byte, error) {
	icons := make(map[string][]byte)
	var err error
	icons["ion"], err = base64.StdEncoding.DecodeString(iconOn)
	if err != nil {
		return nil, err
	}
	icons["ioff"], err = base64.StdEncoding.DecodeString(iconOff)
	if err != nil {
		return nil, err
	}
	icons["ierr"], err = base64.StdEncoding.DecodeString(iconError)
	if err != nil {
		return nil, err
	}
	return icons, nil

}

// create smartlight cache folder and put icon files into cache folder (used for notifications)
func installCacheFiles() (string, error) {
	d, err := os.UserCacheDir()
	if err != nil {
		return "", err
	}
	d += "/smartlight/"

	_, err = os.Stat(d)
	if err != nil {
		if !os.IsNotExist(err) {
			return "", err
		}
		os.Mkdir(d, 0755)
	}

	err = installFile(iconError, "error.svg", d)
	if err != nil {
		return "", err
	}
	err = installFile(iconOk, "ok.svg", d)
	if err != nil {
		return "", err
	}

	return d, nil
}

// puts a file into cache folder
func installFile(content, name, path string) error {
	b, err := base64.StdEncoding.DecodeString(content)
	if err != nil {
		return err
	}

	f := path + name
	_, err = os.Stat(f)
	if err != nil {
		if !os.IsNotExist(err) {
			return err
		}
		err = ioutil.WriteFile(f, b, 0644)
		if err != nil {
			return err
		}
	}
	return nil
}
